import React, { useState } from 'react';
import Dice from '../components/Dice';
import Hourglass from '../components/Hourglass';
import SectorDeck from '../components/SectorDeck';
import './GamePage.css';

const GamePage = () => {
  const [selectedSectors, setSelectedSectors] = useState([]);
  const handleSectorSelection = (sectors) => {
    setSelectedSectors(sectors);
  };

  return (
    <div className="game-container">
       <div className="cards-zone"> {/* Nouvelle zone pour les cartes */}
       <SectorDeck onSelectSectors={handleSectorSelection} />

       </div>
      <div className="game-table">
        {/* Contenu futur du jeu ira ici */}
        <Dice />
        <Hourglass />

        
        
        {/* Zone pour afficher les cartes sélectionnées */}
        <div className="selected-sectors-row">
          {selectedSectors.map((sector, index) => (
            <div 
              key={index} 
              className="sector-card-on-table"
              style={{
                '--recto': `url(${sector})`,
                '--verso': `url(${sector.replace('recto', 'verso')})`
              }}
            >
              <img src={sector} alt={`Secteur ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default GamePage;
