import React, { useState, useEffect } from 'react';
import timerImage from '../assets/icons/timer.png'; // Remplace par le chemin de ton image de sablier
import './Timer.css'; // Nous créerons ce fichier plus tard

const Timer = ({ initialTime = 60 }) => {
  const [time, setTime] = useState(initialTime);

  useEffect(() => {
    const interval = setInterval(() => {
      setTime((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="timer">
      <img src={timerImage} alt="Sablier" />
      <span>{time}</span>
    </div>
  );
};

export default Timer;
