import React, { useState } from 'react';
import './Navbar.css';
import logo from '../assets/logo.png';


// Importation dynamique des images des cartes avec require.context
const importImages = (context) => {
  const images = {};
  context.keys().forEach((item) => {
    const path = item.replace('./', '').replace('.png', '');
    images[path] = context(item);
  });
  return images;
};

const attaqueImages = importImages(require.context('../assets/attaques', false, /\.png$/));
const bouclierImages = importImages(require.context('../assets/boucliers', false, /\.png$/));
const secteurImages = importImages(require.context('../assets/secteurs', false, /\.png$/));
const evenementImages = importImages(require.context('../assets/evenements', false, /\.png$/));

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  
  const [isGuideOpen, setIsGuideOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('attaques'); // Catégorie sélectionnée

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

 
  
  const openGuide = () => {
   
    setIsGuideOpen(true);
  };
  

 

  const closeGuide = () => {
    setIsGuideOpen(false); // Fermer la fenêtre du guide
  };

  const getImagesByCategory = () => {
    switch (selectedCategory) {
      case 'attaques': return attaqueImages;
      case 'boucliers': return bouclierImages;
      case 'secteurs': return secteurImages;
      case 'evenements': return evenementImages;
      default: return {};
    }
  };

  return (
    <>
      <nav className="navbar">
        <div className="navbar-left">
          <button className="menu-button" onClick={toggleMenu}>
            ☰ Menu
          </button>
          {isMenuOpen && (
            <div className="dropdown-menu">
              <a href="/Login"><button>Mon compte</button></a>
              <button>Présentation du jeu</button>
              <button>Fonctionnalités</button>
              <button>À propos de nous</button>
              <a href="/"><button>Quitter</button></a>
            </div>
          )}
          <img src={logo} alt="Cyber@Hack" className="logo" />
          <span className="game-name">Cyber@Hack</span>
        </div>
        <div className="navbar-right">
          <a href="/"><button>Accueil</button></a>
          <span>|</span>
          <a href="/Regles"><button >Règles du jeu</button></a>
          <span>|</span>
          <button onClick={openGuide}>Guide</button>
          <span>|</span>
          <a href="/Login"><button>Compte</button></a>
        </div>
      </nav>

      

      {/* Fenêtre modale du guide */}
      {isGuideOpen && (
        <div className="inline-modal-overlay" onClick={() => setIsGuideOpen(false)}>
          <div className="inline-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-button" onClick={closeGuide}>✖</span>
            <h2>Guide des cartes du jeu</h2>
            <div className="category-buttons">
              <button 
                className={selectedCategory === 'attaques' ? 'selected' : ''} 
                onClick={() => setSelectedCategory('attaques')}
              >
                Cartes Attaques
              </button>
              <button 
                className={selectedCategory === 'boucliers' ? 'selected' : ''} 
                onClick={() => setSelectedCategory('boucliers')}
              >
                Cartes Boucliers
              </button>
              <button 
                className={selectedCategory === 'secteurs' ? 'selected' : ''} 
                onClick={() => setSelectedCategory('secteurs')}
              >
                Cartes Secteurs
              </button>
              <button 
                className={selectedCategory === 'evenements' ? 'selected' : ''} 
                onClick={() => setSelectedCategory('evenements')}
              >
                Cartes Événements
              </button>
            </div>

            <div className="cards-grid">
              {Object.values(getImagesByCategory()).map((img, index) => (
                <img key={index} src={img} alt={`Carte ${index}`} className="card-img" />
              ))}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
