import React from 'react';
import './ShieldDisplay.css';

const ShieldDisplay = ({ shieldPockets }) => {
  return (
    <div className="shield-display">
      {shieldPockets.map((pocket, pocketIndex) => (
        <div key={pocketIndex} className="shield-pocket-display">
          {pocket.map((shield, shieldIndex) => (
            <div 
              key={shieldIndex}
              className="shield-card-display"
              style={{
                '--verso': `url(${shield.verso})`
              }}
            >
              <div className="shield-card-inner">
                <img 
                  src={shield.verso} 
                  alt={`Bouclier ${shield.type} ${shield.value}`} 
                />
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default ShieldDisplay;
