import React from 'react';
import Card from './Card';
import './Deck.css'; // Nous créerons ce fichier plus tard

const Deck = ({ type, cards, onCardClick }) => {
  return (
    <div className="deck">
      <h3>{type}</h3>
      <div className="cards">
        {cards.map((card, index) => (
          <Card key={index} type={type} image={card.image} onClick={() => onCardClick(card)} />
        ))}
      </div>
    </div>
  );
};

export default Deck;
