import React from 'react';
import Dice from './Dice';
import Timer from './Timer';
import './GameTable.css'; // Nous créerons ce fichier plus tard

const GameTable = () => {
  const sectors = Array(10).fill({ image: require('../assets/secteurs/accès.png') }); // Remplace par le chemin de tes cartes secteur
  const shields = Array(40).fill({ image: require('../assets/boucliers/bouclier - dark1.png') }); // Remplace par le chemin de tes cartes bouclier
  const attacks = Array(40).fill({ image: require('../assets/attaques/attak 1 v.png') }); // Remplace par le chemin de tes cartes attaque
  const events = Array(20).fill({ image: require('../assets/evenements/event - recto (1).png') }); // Remplace par le chemin de tes cartes événement

  const handleCardClick = (card) => {
    console.log('Carte cliquée :', card);
  };

  return (
    <div className="game-table">
      <div className="decks">
        <Deck type="Secteurs" cards={sectors} onCardClick={handleCardClick} />
        <Deck type="Boucliers" cards={shields} onCardClick={handleCardClick} />
        <Deck type="Attaques" cards={attacks} onCardClick={handleCardClick} />
        <Deck type="Événements" cards={events} onCardClick={handleCardClick} />
      </div>
      <div className="tools">
        <Dice />
        <Timer />
      </div>
    </div>
  );
};

export default GameTable;
