import React, { useState } from 'react';
import diceImage from '../assets/icons/dice.png'; // Remplace par le chemin de ton image de dé
import './Dice.css'; // Nous créerons ce fichier plus tard

const Dice = () => {
  const [value, setValue] = useState(1);

  const rollDice = () => {
    const randomValue = Math.floor(Math.random() * 6) + 1;
    setValue(randomValue);
  };

  return (
    <div className="dice" onClick={rollDice}>
      <img src={diceImage} alt="Dé" />
      <span>{value}</span>
    </div>
  );
};

export default Dice;
