import React from 'react';
import './GamePage.css';
import tableImage from '../assets/icons/table.jpg'; // Assurez-vous d'avoir cette image

const GamePage = () => {
  return (
    <div className="game-environment">
      {/* Arrière-plan avec effet de lumière bleu/violet */}
      <div className="ambient-light"></div>
      
      {/* Conteneur principal avec l'image de table */}
      <div className="game-table-container">
        <img 
          src={tableImage} 
          alt="Table de jeu CyberHack" 
          className="table-surface"
        />
        
        {/* Zone de jeu délimitée */}
        <div className="play-area">
          {/* Header du jeu */}
          <div className="game-header">
            <h1 className="game-title">
              <span className="cyber-text">CYBER</span>
              <span className="hack-text">@HACK</span>
            </h1>
            <div className="game-status">Tour du Joueur</div>
          </div>
          
          {/* Zone des cartes Secteur */}
          <div className="sectors-grid">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="sector-slot">
                <div className="sector-card">Secteur {i+1}</div>
              </div>
            ))}
          </div>
          
          {/* Zone des joueurs */}
          <div className="players-area">
            <div className="player-side player-you">
              <h3>Votre Main</h3>
              <div className="hand-cards"></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GamePage;
